import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const ListCart = () => {
  const [products, setProducts] = useState([
    {
      id: 1168761308,
      quantity: 2,
      name: 'Vintage Clock',
      category: 'Tech',
      price: 18.34,
      image: 'https://cdn.bootstrapstudio.io/products/product-21_sm.jpg',
      status: 'none', // Trạng thái xử lý
    },
    {
      id: 589605485,
      quantity: 1,
      name: 'Reusable Cup',
      category: 'Cups',
      price: 35.39,
      image: 'https://cdn.bootstrapstudio.io/products/product-18_sm.jpg',
      status: 'none', // Trạng thái xử lý
    },
  ]);

  const handleRemove = (id) => {
    setProducts((prevProducts) =>
      prevProducts.filter((product) => product.id !== id)
    );
  };

  const calculateTotal = () => {
    return products
      .reduce((total, product) => total + product.price * product.quantity, 0)
      .toFixed(2);
  };

  const handleStatusToggle = (id) => {
    setProducts((prevProducts) =>
      prevProducts.map((product) =>
        product.id === id
          ? {
              ...product,
              status:
                product.status === 'none'
                  ? 'pending'
                  : product.status === 'pending'
                  ? 'finished'
                  : 'none',
            }
          : product
      )
    );
  };

  return (
    <div className="container py-5">
      <div className="row mx-auto">
        <div className="col">
          <div className="shopping-cart">
            <div className="cart-header">
              <div className="cart-col-index">#</div>
              <div className="cart-col-product">Product</div>
              <div className="cart-col-price">Price</div>
              <div className="cart-col-quantity">Quantity</div>
              <div className="cart-col-total">Total</div>
              <div className="cart-col-status">Status</div>
            </div>
            <div className="cart-body">
              {products.map((product, index) => (
                <div className="cart-row" key={product.id}>
                  <div className="cart-col-index">{index + 1}</div>
                  <div className="cart-col-product">{product.name}</div>
                  <div className="cart-col-price">${product.price.toFixed(2)}</div>
                  <div className="cart-col-quantity">{product.quantity}</div>
                  <div className="cart-col-total">
                    ${(product.price * product.quantity).toFixed(2)}
                  </div>
                  <div className="cart-col-status">
                    {product.status === 'none' && (
                      <>
                        <button
                          className="btn btn-success btn-sm mx-2"
                          onClick={() => handleStatusToggle(product.id)}
                        >
                          ✔
                        </button>
                        <button
                          className="btn btn-danger btn-sm"
                          onClick={() => handleRemove(product.id)}
                        >
                          ✖
                        </button>
                      </>
                    )}
                    {product.status === 'pending' && (
                      <>
                        <span className="badge bg-warning text-dark">Pending</span>
                        <button
                          className="btn btn-success btn-sm mx-2"
                          onClick={() => handleStatusToggle(product.id)}
                        >
                          ✔
                        </button>
                      </>
                    )}
                    {product.status === 'finished' && (
                      <span className="badge bg-success text-dark">Finished</span>
                    )}
                  </div>
                </div>
              ))}
            </div>
            <div className="cart-footer mt-4">
              <h4>Total: ${calculateTotal()}</h4>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ListCart;
