import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";

const ListCart = () => {

  const fetchOrderDetails = async () => {
    try {
      // Lấy orderId từ localStorage
      const orderId = localStorage.getItem("orderId");
      if (!orderId) {
        throw new Error("Order ID not found in localStorage.");
      }
  
      // Fetch dữ liệu từ các API
      const [orderDetailRes, orderRes, productRes] = await Promise.all([
        fetch("http://localhost:8080/mycoffee/order-detail").then((res) => res.json()),
        fetch("http://localhost:8080/mycoffee/order").then((res) => res.json()),
        fetch("http://localhost:8080/mycoffee/product").then((res) => res.json()),
      ]);
  
      // Kiểm tra phản hồi
      if (
        orderDetailRes.code !== 0 ||
        orderRes.code !== 0 ||
        productRes.code !== 0
      ) {
        throw new Error("Failed to fetch data from one or more endpoints.");
      }
  
      // Lọc các orderDetail theo orderId
      const orderDetails = orderDetailRes.result.filter(
        (detail) => detail.orderId === orderId
      );
  
      // Tìm order theo orderId
      const order = orderRes.result.find((item) => item.orderId === orderId);
      if (!order) {
        throw new Error("Order not found.");
      }
  
      // Map productId trong orderDetails tới product để lấy thông tin sản phẩm
      const products = productRes.result;
      const mappedOrderDetails = orderDetails.map((detail) => {
        const product = products.find((p) => p.id === detail.productId);
        return {
          productId: detail.productId,
          name: product?.name || "Unknown Product",
          price: product?.price || 0,
          quantity: detail.quantity,
          image_url: product?.image_url || "",
        };
      });
  
      // Chuẩn bị dữ liệu cuối cùng
      const orderData = {
        orderId: order.orderId,
        table: order.table,
        totalPrice: order.totalPrice,
        details: mappedOrderDetails,
      };
  
      return orderData; // Trả về dữ liệu cuối cùng
    } catch (error) {
      console.error("Error fetching order details:", error);
      alert("Failed to fetch order details. Please try again.");
      return null; // Trả về null nếu có lỗi
    }
  };
  
  const [orderData, setOrderData] = useState(null);

  useEffect(() => {
    const loadOrderDetails = async () => {
      const data = await fetchOrderDetails();
      setOrderData(data);
    };
    loadOrderDetails();
  }, []);
  
  const [products, setProducts] = useState([
    {
      id: 1168761308,
      quantity: 2,
      name: '1. Vintage Clock',
      category: 'Tech',
      price: 18.34,
      image: 'https://cdn.bootstrapstudio.io/products/product-21_sm.jpg',
      status: 'none', // Trạng thái xử lý
    },
    {
      id: 589605485,
      quantity: 1,
      name: '2. Reusable Cup',
      category: 'Cups',
      price: 35.39,
      image: 'https://cdn.bootstrapstudio.io/products/product-18_sm.jpg',
      status: 'none', // Trạng thái xử lý
    },
  ]);

  const handleRemove = (id) => {
    setProducts((prevProducts) =>
      prevProducts.filter((product) => product.id !== id)
    );
  };

  const handleStatusToggle = (id) => {
    setProducts((prevProducts) =>
      prevProducts.map((product) =>
        product.id === id
          ? {
              ...product,
              status:
                product.status === 'none'
                  ? 'pending'
                  : product.status === 'pending'
                  ? 'finished'
                  : 'none',
            }
          : product
      )
    );
  };

  return (
    <div className="container py-5">
      <div className="row">
        <div className="col">
          <div className="shopping-cart">
            {/* Header */}
            <div className="d-flex fw-bold border-bottom pb-2">
              <div className="col-1 text-center border-end">Table</div>
              <div className="col-4 ps-3 border-end">Product</div>
              <div className="col-2 text-center border-end">Price</div>
              <div className="col-2 text-center border-end">Quantity</div>
              <div className="col-2 text-center border-end">Total</div>
              <div className="col-1 text-center">Status</div>
            </div>
            {/* Body */}
            {products.map((product, index) => (
              <div className="d-flex align-items-center py-2 border-bottom" key={product.id}>
                <div className="col-1 text-center border-end">{index + 1}</div>
                <div className="col-4 ps-3 border-end">{product.name}</div>
                <div className="col-2 text-center border-end">${product.price.toFixed(2)}</div>
                <div className="col-2 text-center border-end">{product.quantity}</div>
                <div className="col-2 text-center border-end">
                  ${(product.price * product.quantity).toFixed(2)}
                </div>
                <div className="col-1 text-center">
                  {product.status === 'none' && (
                    <>
                      <button
                        className="btn btn-success btn-sm mx-1"
                        onClick={() => handleStatusToggle(product.id)}
                      >
                        ✔
                      </button>
                      <button
                        className="btn btn-danger btn-sm"
                        onClick={() => handleRemove(product.id)}
                      >
                        ✖
                      </button>
                    </>
                  )}
                  {product.status === 'pending' && (
                    <>
                      <span className="badge bg-warning text-dark">Pending</span>
                      <button
                        className="btn btn-success btn-sm mx-1"
                        onClick={() => handleStatusToggle(product.id)}
                      >
                        ✔
                      </button>
                    </>
                  )}
                  {product.status === 'finished' && (
                    <span className="badge bg-success text-dark">Finished</span>
                  )}
                </div>
              </div>
            ))}

          </div>
        </div>
      </div>
    </div>
  );
};

export default ListCart;
