import React from "react";
import "bootstrap/dist/css/bootstrap.min.css";
import { useEffect, useState } from "react";

const ListCart = () => {
  const [orders, setOrders] = useState([]);

  const fetchOrders = async () => {
    try {
      const response = await fetch(
        "http://localhost:8080/mycoffee/order/with-details"
      );
      const data = await response.json();

      const ordersData = data.result || []; // Lấy mảng đơn hàng từ API

      // Ánh xạ đơn hàng
      const mappedOrders = ordersData.map((order) => ({
        orderId: order.orderId,
        table: order.table,
        status: "none", // Trạng thái của toàn bộ đơn hàng
        orderDetails: order.orderDetails.map((detail) => ({
          quantity: detail.quantity,
          productName: detail.productName,
          image: detail.image,
          price: detail.price,
          status: "none", // Trạng thái của sản phẩm
        })),
      }));

      setOrders(mappedOrders);
    } catch (error) {
      console.error("Failed to fetch orders:", error);
    }
  };

  useEffect(() => {
    fetchOrders();
  }, []);

  const handleRemove = (orderId, productName) => {
    setOrders((prevOrders) =>
      prevOrders.map((order) =>
        order.orderId === orderId
          ? {
              ...order,
              orderDetails: order.orderDetails.filter(
                (detail) => detail.productName !== productName
              ),
            }
          : order
      )
    );
  };

  const handleStatusToggle = (orderId) => {
    setOrders((prevOrders) =>
      prevOrders.map((order) =>
        order.orderId === orderId
          ? {
              ...order,
              status:
                order.status === "none"
                  ? "pending"
                  : order.status === "pending"
                  ? "finished"
                  : "none",
            }
          : order
      )
    );
  };

  return (
    <div className="container py-5">
      <div className="row">
        <div className="col">
          <div className="shopping-cart">
            {/* Header */}
            <div className="d-flex fw-bold border-bottom pb-2">
              <div className="col-1 text-center border-end">Table</div>
              <div className="col-4 ps-3 border-end">Product</div>
              <div className="col-2 text-center border-end">Price</div>
              <div className="col-2 text-center border-end">Quantity</div>
              <div className="col-2 text-center border-end">Total</div>
              <div className="col-1 text-center">Status</div>
            </div>
            {/* Body */}
            {orders.map((order) => {
              // Tính tổng giá trị của đơn hàng
              const total = order.orderDetails.reduce(
                (acc, detail) => acc + detail.price * detail.quantity,
                0
              );

              return (
                <React.Fragment key={order.orderId}>
                  {/* Thông tin chung của order */}
                  <div className="d-flex align-items-center py-2 border-bottom bg-light">
                    <div className="col-1 text-center border-end">
                      {order.table}
                    </div>
                    <div className="col-4 ps-3 border-end">
                      <strong>Order #{order.orderId}</strong>
                    </div>
                    <div className="col-2 text-center border-end"></div>
                    <div className="col-2 text-center border-end"></div>
                    <div className="col-2 text-center border-end">
                      <strong>${total.toFixed(2)}</strong>
                    </div>
                    <div className="col-1 text-center">
                      {order.status === "none" && (
                        <button
                          className="btn btn-success btn-sm"
                          onClick={() => handleStatusToggle(order.orderId)}
                        >
                          Mark Pending
                        </button>
                      )}
                      {order.status === "pending" && (
                        <button
                          className="btn btn-warning btn-sm"
                          onClick={() => handleStatusToggle(order.orderId)}
                        >
                          Mark Finished
                        </button>
                      )}
                      {order.status === "finished" && (
                        <span className="badge bg-success">Finished</span>
                      )}
                    </div>
                  </div>
                  {/* Chi tiết các sản phẩm trong order */}
                  {order.orderDetails.map((detail, index) => (
                    <div
                      className="d-flex align-items-center py-2 border-bottom"
                      key={`${order.orderId}-${index}`}
                    >
                      <div className="col-1 text-center border-end"></div>
                      <div className="col-4 ps-3 border-end">
                        {detail.productName}
                      </div>
                      <div className="col-2 text-center border-end">
                        ${detail.price.toFixed(2)}
                      </div>
                      <div className="col-2 text-center border-end">
                        {detail.quantity}
                      </div>
                      <div className="col-2 text-center border-end">
                        ${(detail.price * detail.quantity).toFixed(2)}
                      </div>
                      <div className="col-1 text-center">
                        {detail.status === "none" && (
                          <button
                            className="btn btn-danger btn-sm"
                            onClick={() =>
                              handleRemove(order.orderId, detail.productName)
                            }
                          >
                            Remove
                          </button>
                        )}
                      </div>
                    </div>
                  ))}
                </React.Fragment>
              );
            })}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ListCart;
