import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const ListCart = () => {
  const [products, setProducts] = useState([
    {
      id: 1168761308,
      quantity: 2,
      name: 'Vintage Clock',
      category: 'Tech',
      price: 18.34,
      image: 'https://cdn.bootstrapstudio.io/products/product-21_sm.jpg',
      status: 'none',
    },
    {
      id: 589605485,
      quantity: 1,
      name: 'Reusable Cup',
      category: 'Cups',
      price: 35.39,
      image: 'https://cdn.bootstrapstudio.io/products/product-18_sm.jpg',
      status: 'none',
    },
  ]);

  const handleIncrease = (id) => {
    setProducts((prevProducts) =>
      prevProducts.map((product) =>
        product.id === id
          ? { ...product, quantity: product.quantity + 1 }
          : product
      )
    );
  };

  const handleDecrease = (id) => {
    setProducts((prevProducts) =>
      prevProducts.map((product) =>
        product.id === id && product.quantity > 1
          ? { ...product, quantity: product.quantity - 1 }
          : product
      )
    );
  };

  const handleRemove = (id) => {
    setProducts((prevProducts) =>
      prevProducts.filter((product) => product.id !== id)
    );
  };

  const handleStatusToggle = (id) => {
    setProducts((prevProducts) =>
      prevProducts.map((product) =>
        product.id === id
          ? {
              ...product,
              status:
                product.status === 'none'
                  ? 'pending'
                  : product.status === 'pending'
                  ? 'finished'
                  : 'none',
            }
          : product
      )
    );
  };

  return (
    <div className="container py-5">
      <div className="row">
        <div className="col">
          <table className="table table-striped text-center">
            <thead>
              <tr>
                <th style={{ width: '5%' }}>#</th>
                <th style={{ width: '30%' }}>Product</th>
                <th style={{ width: '15%' }}>Price</th>
                <th style={{ width: '15%' }}>Quantity</th>
                <th style={{ width: '15%' }}>Total</th>
                <th style={{ width: '20%' }}>Processing</th>
              </tr>
            </thead>
            <tbody>
              {products.map((product, index) => (
                <tr key={product.id}>
                  <td>{index + 1}</td>
                  <td>{product.name}</td>
                  <td>${product.price.toFixed(2)}</td>
                  <td>{product.quantity}</td>
                  <td>${(product.price * product.quantity).toFixed(2)}</td>
                  <td>
                    {product.status === 'none' && (
                      <>
                        <button
                          className="btn btn-success btn-sm mx-1"
                          onClick={() => handleStatusToggle(product.id)}
                        >
                          ✔
                        </button>
                        <button
                          className="btn btn-danger btn-sm"
                          onClick={() => handleRemove(product.id)}
                        >
                          ✖
                        </button>
                      </>
                    )}
                    {product.status === 'pending' && (
                      <>
                        <span style={{ color: 'black' }}>Pending</span>
                        <button
                          className="btn btn-success btn-sm mx-1"
                          onClick={() => handleStatusToggle(product.id)}
                        >
                          ✔
                        </button>
                      </>
                    )}
                    {product.status === 'finished' && (
                      <span style={{ color: 'black' }}>Finished</span>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          <div className="mt-4 text-end">
            <h4>
              Total: $
              {products
                .reduce(
                  (total, product) =>
                    total + product.price * product.quantity,
                  0
                )
                .toFixed(2)}
            </h4>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ListCart;
