import React from 'react';
import { Helmet } from 'react-helmet';
import 'bootstrap/dist/css/bootstrap.min.css';
import NavBar from './components/NavBar';

function MenuPage() {
//   return (
//     <div style={{ padding: '50px', display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
//       <div style={{ flex: 1 }}>
//         <Search />
//       </div>
//       <div style={{ flex: 1, textAlign: 'right' }}>
//         <MenuTab />
//       </div>
//     </div>
    
// );
// }

return (
  <>
    <Helmet>
      <link rel="stylesheet" href="assets/bootstrap/css/bootstrap.min.css?h=ae25e30cd2ef0f4f5b70ebf4c668048b" />
    </Helmet>
    <NavBar showSearch={true} />
    <section className="py-5">
      <div className="container py-5">
        <div className="row mb-4 mb-lg-5">
          <div className="col-md-8 col-xl-6 text-center mx-auto">
            <h2 className="fw-bold">Products</h2>
            <p className="text-muted">No matter the project, our team can handle it.&nbsp;</p>
          </div>
        </div>
        <div className="row mx-auto">
          <div className="col">
            <div data-reflow-type="product-list" data-reflow-layout="cards" data-reflow-order="date_desc" data-reflow-product-link="/product.html?product={id}"></div>
          </div>
        </div>
      </div>
    </section>
    <style jsx>{`
      .search-box {
        height: 45px;
        border-radius: 5px;
        background-color: #333333;
      }
  
      .input {
        color: #cccccc;
        background-color: #333333;
        border: none;
        outline: none;
        padding: 5px;
      }
  
      .input::placeholder {
        color: #808080;
      }
  
      .btn-icon-content {
        background-color: transparent;
        border: none;
        cursor: pointer;
      }
  
      .search-icon svg {
        width: 22px;
        height: 30px;
        fill: #ffffff;
      }
      `}</style>
    
  </>
);
}

export default MenuPage;
