import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const ListCart = () => {
  const [products, setProducts] = useState([
    {
      id: 1168761308,
      quantity: 2,
      name: 'Vintage Clock',
      category: 'Tech',
      price: 18.34,
      image: 'https://cdn.bootstrapstudio.io/products/product-21_sm.jpg',
      status: 'none', // Trạng thái xử lý
    },
    {
      id: 589605485,
      quantity: 1,
      name: 'Reusable Cup',
      category: 'Cups',
      price: 35.39,
      image: 'https://cdn.bootstrapstudio.io/products/product-18_sm.jpg',
      status: 'none', // Trạng thái xử lý
    },
  ]);

  const handleRemove = (id) => {
    setProducts((prevProducts) =>
      prevProducts.filter((product) => product.id !== id)
    );
  };

  const calculateTotal = () => {
    return products
      .reduce((total, product) => total + product.price * product.quantity, 0)
      .toFixed(2);
  };

  const handleStatusToggle = (id) => {
    setProducts((prevProducts) =>
      prevProducts.map((product) =>
        product.id === id
          ? {
              ...product,
              status:
                product.status === 'none'
                  ? 'pending'
                  : product.status === 'pending'
                  ? 'finished'
                  : 'none',
            }
          : product
      )
    );
  };

  return (
    <div className="container py-5">
      <div className="row mx-auto">
        <div className="col">
          <div data-reflow-type="shopping-cart">
            <div className="reflow-shopping-cart" style={{ display: 'block' }}>
              <div className="ref-loading-overlay"></div>
              <div className="ref-message" style={{ display: 'none' }}>
                Unable to load shopping cart.
              </div>
              <div className="ref-cart" style={{ display: 'block' }}>
                <div className="ref-th">
                  <div className="ref-table-col">Table</div>
                  <div className="ref-product-col">Product</div>
                  <div className="ref-price-col">Price</div>
                  <div className="ref-quantity-col">Quantity</div>
                  <div className="ref-total-col">Total</div>
                  <div className="ref-total-col">Processing</div>
                </div>
                <div className="ref-cart-table">
                  {products.map((product, index) => (
                    <div
                      className="ref-product"
                      key={product.id}
                      data-id={product.id}
                      data-quantity={product.quantity}
                    >
                      <div className="ref-table-col">{index + 1}</div>
                      <div className="ref-product-col">
                        <div className="ref-product-name">{product.name}</div>
                      </div>
                      <div className="ref-price-col">
                        <div className="ref-product-price">
                          ${product.price.toFixed(2)}
                        </div>
                      </div>
                      <div className="ref-quantity-col">
                        <div className="ref-quantity">
                          {product.quantity}
                        </div>
                      </div>
                      <div className="ref-total-col">
                        <div className="ref-product-total">
                          <div className="ref-product-total-sum">
                            ${(
                              product.price * product.quantity
                            ).toFixed(2)}
                          </div>
                        </div>
                      </div>
                      <div className="ref-total-col">
                        {product.status === 'none' && (
                          <>
                            <button
                              className="btn btn-success btn-sm mx-2"
                              onClick={() => handleStatusToggle(product.id)}
                            >
                              ✔
                            </button>
                            <button
                              className="btn btn-danger btn-sm"
                              onClick={() => handleRemove(product.id)}
                            >
                              ✖
                            </button>
                          </>
                        )}
                        {product.status === 'pending' && (
                          <>
                            <span className="badge bg-warning" style={{ color: 'black' }}>Pending</span>
                            <button
                              className="btn btn-success btn-sm mx-2"
                              onClick={() => handleStatusToggle(product.id)}
                            >
                              ✔
                            </button>
                          </>
                        )}
                        {product.status === 'finished' && (
                          <span className="badge bg-success" style={{ color: 'black' }}>Finished</span>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
                <div className="mt-4">
                  <h4>Total: ${calculateTotal()}</h4>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ListCart;
