import React, { useEffect } from 'react';
import { Helmet } from 'react-helmet';
import 'bootstrap/dist/css/bootstrap.min.css';
import Search from './components/Search';
import MenuTab from './components/MenuTab';
import NavBar from './components/NavBar';

function MenuPage() {
//   return (
//     <div style={{ padding: '50px', display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
//       <div style={{ flex: 1 }}>
//         <Search />
//       </div>
//       <div style={{ flex: 1, textAlign: 'right' }}>
//         <MenuTab />
//       </div>
//     </div>
    
//   );
// }

useEffect(() => {
  // Đảm bảo rằng script được thêm vào body khi component được render
  const script1 = document.createElement('script');
  script1.src = "assets/bootstrap/js/bootstrap.min.js?h=374d178d651fa0eaf680a1fa7b40c788";
  script1.async = true;
  document.body.appendChild(script1);

  const script2 = document.createElement('script');
  script2.src = "https://cdn.reflowhq.com/v2/toolkit.min.js";
  script2.async = true;
  document.body.appendChild(script2);

  const script3 = document.createElement('script');
  script3.src = "assets/js/bs-init.js?h=4f6030575bc01c04c2cd8963f3a0d244";
  script3.async = true;
  document.body.appendChild(script3);

  const script4 = document.createElement('script');
  script4.src = "assets/js/bold-and-dark.js?h=78569998362133b84a76614652f3624f";
  script4.async = true;
  document.body.appendChild(script4);

  // Cleanup khi component bị unmount
  return () => {
    document.body.removeChild(script1);
    document.body.removeChild(script2);
    document.body.removeChild(script3);
    document.body.removeChild(script4);
  };
}, []);

return (
  <>
    <Helmet>
      <meta charset="utf-8" />
      <meta name="viewport" content="width=device-width, initial-scale=1.0, shrink-to-fit=no" />
      <title>Products - Brand</title>
      <link rel="stylesheet" href="assets/bootstrap/css/bootstrap.min.css?h=ae25e30cd2ef0f4f5b70ebf4c668048b" />
      <link rel="stylesheet" href="https://cdn.reflowhq.com/v2/toolkit.min.css" />
      <link rel="stylesheet" href="assets/css/Inter.css?h=b1e57a7467f5afe875adbba098128f1d" />
    </Helmet>
    <nav className="navbar navbar-expand-md sticky-top py-3 navbar-dark" id="mainNav">
      <div className="container">
        <a className="navbar-brand d-flex align-items-center">
          <span className="bs-icon-sm bs-icon-circle bs-icon-primary shadow d-flex justify-content-center align-items-center me-2 bs-icon">
            <svg xmlns="http://www.w3.org/2000/svg" width="1em" height="1em" fill="currentColor" viewBox="0 0 16 16" className="bi bi-bezier">
              <path fillRule="evenodd" d="M0 10.5A1.5 1.5 0 0 1 1.5 9h1A1.5 1.5 0 0 1 4 10.5v1A1.5 1.5 0 0 1 2.5 13h-1A1.5 1.5 0 0 1 0 11.5zm1.5-.5a.5.5 0 0 0-.5.5v1a.5.5 0 0 0 .5.5h1a.5.5 0 0 0 .5-.5v-1a.5.5 0 0 0-.5-.5zm10.5.5A1.5 1.5 0 0 1 13.5 9h1a1.5 1.5 0 0 1 1.5 1.5v1a1.5 1.5 0 0 1-1.5 1.5h-1a1.5 1.5 0 0 1-1.5-1.5zm1.5-.5a.5.5 0 0 0-.5.5v1a.5.5 0 0 0 .5.5h1a.5.5 0 0 0 .5-.5v-1a.5.5 0 0 0-.5-.5zM6 4.5A1.5 1.5 0 0 1 7.5 3h1A1.5 1.5 0 0 1 10 4.5v1A1.5 1.5 0 0 1 8.5 7h-1A1.5 1.5 0 0 1 6 5.5zM7.5 4a.5.5 0 0 0-.5.5v1a.5.5 0 0 0 .5.5h1a.5.5 0 0 0 .5-.5v-1a.5.5 0 0 0-.5-.5z"></path>
            </svg>
          </span>
          <span>Brand</span>
        </a>
        <button data-bs-toggle="collapse" className="navbar-toggler" data-bs-target="#navcol-1">
          <span className="visually-hidden">Toggle navigation</span>
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navcol-1">
          <div className="d-flex justify-content-center align-items-center mx-auto">
            <div className="search-box d-flex align-items-center border">
              <input placeholder="Search..." className="input" type="text" />
              <button className="btn-icon-content p-2" aria-label="Search">
                <span className="search-icon">
                  <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 512 512">
                    <path d="M416 208c0 45.9-14.9 88.3-40 122.7L502.6 457.4c12.5 12.5 12.5 32.8 0 45.3s-32.8 12.5-45.3 0L330.7 376c-34.4 25.2-76.8 40-122.7 40C93.1 416 0 322.9 0 208S93.1 0 208 0S416 93.1 416 208zM208 352a144 144 0 1 0 0-288 144 144 0 1 0 0 288z"></path>
                  </svg>
                </span>
              </button>
            </div>
          </div>
          <ul className="navbar-nav mx-auto">
            <li className="nav-item"><a className="nav-link active" href="products.html">Products</a></li>
            <li className="nav-item"><a className="nav-link" href="shopping-cart.html">Shopping Cart</a></li>
          </ul>
          <a className="btn btn-primary shadow" role="button" href="signup.html">Sign up</a>
        </div>
      </div>
    </nav>

    <section className="py-5">
      <div className="container py-5">
        <div className="row mb-4 mb-lg-5">
          <div className="col-md-8 col-xl-6 text-center mx-auto">
            <h2 className="fw-bold">Products</h2>
            <p className="text-muted">No matter the project, our team can handle it.&nbsp;</p>
          </div>
        </div>
        <div className="row mx-auto">
          <div className="col">
            <div data-reflow-type="product-list" data-reflow-layout="cards" data-reflow-order="date_desc" data-reflow-product-link="/product.html?product={id}"></div>
          </div>
        </div>
      </div>
    </section>
    <style jsx>{`
      .search-box {
        height: 45px;
        border-radius: 5px;
        background-color: #333333;
      }
  
      .input {
        color: #cccccc;
        background-color: #333333;
        border: none;
        outline: none;
        padding: 5px;
      }
  
      .input::placeholder {
        color: #808080;
      }
  
      .btn-icon-content {
        background-color: transparent;
        border: none;
        cursor: pointer;
      }
  
      .search-icon svg {
        width: 21px;
        height: 21px;
        fill: #ffffff;
      }
      `}</style>
    
  </>
);
}

export default MenuPage;
