import React from 'react';
import { useNavigate } from 'react-router-dom';

const Logout = () => {
  const navigate = useNavigate();

  const handleLogout = async () => {
    try {
      const authToken = localStorage.getItem('authToken');  // Lấy authToken từ localStorage
      if (!authToken) {
        console.error('No authToken found');
        return;
      }

      // Gửi yêu cầu POST đến /auth/logout
      const response = await fetch('http://localhost:8080/mycoffee/auth/logout', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ authToken }), // Gửi authToken trong body
      });

      if (!response.ok) {
        throw new Error('Logout failed');
      }

      // Xóa authToken sau khi logout
      localStorage.removeItem('authToken');

      // Chuyển hướng về trang chủ
      window.location.href = '/';
    } catch (error) {
      console.error('Logout error:', error);
    }
  };

  return (
    <div className="container-fluid">
      {/* Button fixed ở góc trên bên phải */}
      <div className="position-fixed top-0 end-0 m-3">
        <button
          className="btn btn-primary shadow"
          type="button"
          onClick={handleLogout}  // Gọi hàm handleLogout khi bấm vào button
        >
          Log out
        </button>
      </div>
    </div>
  );
};

export default Logout;
