import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

const ListCart = () => {
  const [products, setProducts] = useState([
    {
      id: 1168761308,
      quantity: 2,
      name: 'Vintage Clock',
      category: 'Tech',
      price: 18.34,
      image: 'https://cdn.bootstrapstudio.io/products/product-21_sm.jpg',
      status: 'none',
    },
    {
      id: 589605485,
      quantity: 1,
      name: 'Reusable Cup',
      category: 'Cups',
      price: 35.39,
      image: 'https://cdn.bootstrapstudio.io/products/product-18_sm.jpg',
      status: 'none',
    },
  ]);

  const handleIncrease = (id) => {
    setProducts((prevProducts) =>
      prevProducts.map((product) =>
        product.id === id
          ? { ...product, quantity: product.quantity + 1 }
          : product
      )
    );
  };

  const handleDecrease = (id) => {
    setProducts((prevProducts) =>
      prevProducts.map((product) =>
        product.id === id && product.quantity > 1
          ? { ...product, quantity: product.quantity - 1 }
          : product
      )
    );
  };

  const handleRemove = (id) => {
    setProducts((prevProducts) =>
      prevProducts.filter((product) => product.id !== id)
    );
  };

  const calculateTotal = () => {
    return products
      .reduce((total, product) => total + product.price * product.quantity, 0)
      .toFixed(2);
  };

  const handleStatusToggle = (id) => {
    setProducts((prevProducts) =>
      prevProducts.map((product) =>
        product.id === id
          ? {
              ...product,
              status:
                product.status === 'none'
                  ? 'pending'
                  : product.status === 'pending'
                  ? 'finished'
                  : 'none',
            }
          : product
      )
    );
  };

  return (
    <div className="container py-5">
      <div className="row mx-auto">
        <div className="col">
          <div className="shopping-cart">
            <div className="cart-header">
              <div className="cart-column">#</div>
              <div className="cart-column">Product</div>
              <div className="cart-column">Price</div>
              <div className="cart-column">Quantity</div>
              <div className="cart-column">Total</div>
              <div className="cart-column">Status</div>
            </div>
            <div className="cart-body">
              {products.map((product, index) => (
                <div className="cart-row" key={product.id}>
                  <div className="cart-column">{index + 1}</div>
                  <div className="cart-column">{product.name}</div>
                  <div className="cart-column">${product.price.toFixed(2)}</div>
                  <div className="cart-column">{product.quantity}</div>
                  <div className="cart-column">
                    ${(product.price * product.quantity).toFixed(2)}
                  </div>
                  <div className="cart-column">
                    {product.status === 'none' && (
                      <>
                        <button
                          className="btn btn-success btn-sm mx-2"
                          onClick={() => handleStatusToggle(product.id)}
                        >
                          ✔
                        </button>
                        <button
                          className="btn btn-danger btn-sm"
                          onClick={() => handleRemove(product.id)}
                        >
                          ✖
                        </button>
                      </>
                    )}
                    {product.status === 'pending' && (
                      <>
                        <span
                          className="badge bg-warning text-dark"
                          style={{ color: 'black' }}
                        >
                          Pending
                        </span>
                        <button
                          className="btn btn-success btn-sm mx-2"
                          onClick={() => handleStatusToggle(product.id)}
                        >
                          ✔
                        </button>
                      </>
                    )}
                    {product.status === 'finished' && (
                      <span
                        className="badge bg-success text-dark"
                        style={{ color: 'black' }}
                      >
                        Finished
                      </span>
                    )}
                  </div>
                </div>
              ))}
            </div>
            <div className="mt-4">
              <h4>Total: ${calculateTotal()}</h4>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ListCart;
