import React from 'react';
import { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import Order from "../ModelFE/Order";
const ListMenu = () => {
  const [products, setProducts] = useState([]);

  const fetchProducts = async () => {
      try {
        const response = await fetch("http://localhost:8080/mycoffee/product");
        const data = await response.json();
      
        // Kiểm tra xem có trường result không
        const productsData = data.result || []; // Lấy mảng sản phẩm từ trường 'result'
    
        // Ánh xạ sản phẩm
        const mappedProducts = productsData.map((item) => ({
          id: item.id,
          name: item.name,
          price: item.price,
          category: item.category.name,
          image_url: item.image_url,
          createdAt: new Date(item.createdAt),
          updatedAt: new Date(item.updatedAt),
          deletedAt: item.deletedAt ? new Date(item.deletedAt) : null,
        }));
    
        setProducts(mappedProducts);
      } catch (error) {
        console.error("Failed to fetch products:", error);
      }
    };

  useEffect(() => {
    fetchProducts();
  }, []);



  const location = useLocation();

  // Lấy số bàn từ URL hoặc localStorage
  const getTableFromUrlOrStorage = () => {
    // Kiểm tra nếu số bàn đã được lưu trong localStorage
    const storedTable = localStorage.getItem("table");
    if (storedTable) {
      return parseInt(storedTable); // Sử dụng số bàn từ localStorage nếu có
    }

    // Nếu chưa có, lấy từ URL
    const params = new URLSearchParams(location.search);
    const tableFromUrl = parseInt(params.get("table")) || 0;

    // Lưu số bàn vào localStorage để dùng lần sau
    localStorage.setItem("table", tableFromUrl);

    return tableFromUrl; // Trả về số bàn lấy từ URL
  };

  // Gọi hàm để lấy số bàn
  const table = getTableFromUrlOrStorage();

  const handleAddToOrder = async (productId) => {
    try {
      // Kiểm tra nếu đã có orderId trong localStorage
      let orderId = localStorage.getItem("orderId");

      // Nếu chưa có orderId, gọi API để tạo mới
      if (!orderId) {
        const response = await fetch("http://localhost:8080/mycoffee/order", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ table }), // Gửi số bàn đến backend
        });

        if (!response.ok) {
          throw new Error("Failed to create order");
        }

        const data = await response.json();
        orderId = data.result.orderId; // Lấy orderId từ phản hồi API

        // Lưu orderId vào localStorage
        localStorage.setItem("orderId", orderId);
      }

      // Lưu thông tin sản phẩm và số bàn vào localStorage
      const currentOrder = JSON.parse(localStorage.getItem("currentOrder")) || [];
      currentOrder.push({ productId, table, orderId });
      localStorage.setItem("currentOrder", JSON.stringify(currentOrder));

      alert(`Product ${productId} added to order ${orderId} in table ${table}!`);
    } catch (error) {
      console.error("Error adding product to order:", error);
    }
  };








  return (
    // <div>
    //   <h1>Menu</h1>
    //   <p>Table: {table}</p>
    //   <ul>
    //     {/* Giả sử bạn có danh sách sản phẩm */}
    //     {[
    //       { id: "product1", name: "Cam ep binh thuong" },
    //       { id: "product2", name: "Cam ep bat thuong" },
    //     ].map((product) => (
    //       <li key={product.id}>
    //         <h2>{product.name}</h2>
    //         <button onClick={() => handleAddToOrder(product.id)}>
    //           Add to Order
    //         </button>
    //       </li>
    //     ))}
    //   </ul>
    // </div>
        <div class="container py-5">
            <div class="row mx-auto">
                <div class="col">
                    <div data-reflow-type="product-list" data-reflow-layout="cards" data-reflow-order="date_desc" data-reflow-product-link="/product.html?product={id}">

                        <div class="reflow-product-list ref-cards">
                        

                        <div class="ref-products">
                        {products.map((product) => (
                            <a class="ref-product" key={product.id}>
                            
                                    <div class="ref-media"><img class="ref-image" src={product.image_url} loading="lazy" />
                                    </div>
                                    <div class="ref-product-data">
                                        <div class="ref-product-info">
                                            <h5 class="ref-name">{product.name}</h5>
                                            </div><strong class="ref-price">${product.price}</strong>
                                    </div>
                                    <div class="ref-addons"><a class="ref-button preview-toggle" href="#" onClick={() => handleAddToOrder(product.id)}>Add to Cart</a></div>
                                </a>))}
                            </div>


                                
                            <div class="ref-product-preview">
                                <div class="ref-product-preview-header">
                                    <div class="ref-title"></div>
                                    <div class="ref-close-button">×</div>
                                </div>
                                <div class="ref-product-preview-content"></div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
  );
};

export default ListMenu;
